mapper.select:

select count(*) 
  from project
 where project_name = #{projectName}


       select a.project_seq,a.team_id,a.project_name,a.finish_yn,a.percentage,a.create_date,b.user_seq as team_leader_seq,b.team_category as team_category
	      from project a left outer join team_member b on a.team_id = b.team_id and b.team_leader = 'Y'
        where a.project_seq = #{projectSeq}
